end

dreadnought.combateer = dreadnought.combateer or {
	targets = {},
	flags = {
		targetAcquired = false,
		canVortex = false,
		vortexCalled = false,
		shockwaveCalled = false
	}
}

Script.script:
function action(name, priority, command) assert(type(name) == "string") assert(type(priority) == "number") assert(type(command) == "function" or type(command) == "string")
local aethercraftCommand = function() end
if type(command) == "string" then aethercraftCommand = function() send(command) end else aethercraftCommand = function() command() end end
local action = { name = name, priority = priority, command = aethercraftCommand }
setmetatable(action, { __lt = function(a, b) return a.priority < b.priority end })
return action end
