Trigger.script:
local timerCommand = function() if dreadnought.aetherbeastCount == 0 then send("shipt Siphon") end end
if dreadnought.piloting.siphonCallout then killTimer("dreadnought.piloting.calloutTimer")
dreadnought.piloting.calloutTimer = tempTimer(.01, timerCommand) dreadnought.piloting.calloutTimer = tempTimer(2, timerCommand) dreadnought.piloting.calloutTimer = tempTimer(4, timerCommand) dreadnought.piloting.calloutTimer = tempTimer(6, timerCommand) dreadnought.piloting.calloutTimer = tempTimer(8, timerCommand) end

Alias.script:
if matches[2] == "on" then enableTrigger("Autopilot") send("config shipsight 1") send("config aethermap 0")
cecho("\n<green>Autopilot system engaged.") else disableTrigger("Autopilot") disableTrigger("Siphon Callout") send("config shipsight 1") send("config aethermap 1")
cecho("\n<green>Autopilot system disengaged.") end
dreadnought.aetherbeastCount = 0 dreadnought.piloting = { shocked = false, siphonCallout = false, calloutTimer = 0, aetherbeastPresent = false, currentPosition = 0, lastMove = "southwest" }

