Trigger.script:
local timerCommand = function () if dreadnought.aetherbeastCount == 0 then send("shipt Siphon") end end
if dreadnought.pilot.siphonCallout then killTimer("dreadnought.pilot.calloutTimer")
dreadnought.pilot.calloutTimer = tempTimer(.01, timerCommand) dreadnought.pilot.calloutTimer = tempTimer(2, timerCommand) dreadnought.pilot.calloutTimer = tempTimer(4, timerCommand) dreadnought.pilot.calloutTimer = tempTimer(6, timerCommand) dreadnought.pilot.calloutTimer = tempTimer(8, timerCommand) end

Alias.script:
if matches[2] == "on" then enableTrigger("Autopilot") send("config shipsight 1") send("config aethermap 0")
dmsg("Autopilot activated.") else disableTrigger("Autopilot") disableTrigger("Siphon Callout") send("config shipsight 1") send("config aethermap 1")
dmsg("Autopilot deactivated.") end

ScriptGroup.script:
dreadnought = dreadnought or {
	version = "1.0",
	aetherbeastCount = 0,
	balance = true,
	skills = {}
}

local reset = function () dmsg("All pilot variables have been reset.") dreadnought.aetherbeastCount = 0
return { shocked = false, siphonCallout = false, shockwaveTimers = {}, calloutTimer = 0, aetherbeastPresent = false, currentPosition = 1,
paths = {} } end
dreadnought.pilot = dreadnought.pilot or { shocked = false, siphonCallout = false, shockwaveTimers = {}, calloutTimer = 0, aetherbeastPresent = false, currentPosition = 1,
paths = {},
reset = function () dreadnought.pilot = reset() dreadnought.pilot.reset = reset end }

local reset = function ()
	dmsg("All empath variables have been reset.")
	dreadnought.aetherbeastCount = 0

	return {
		modules = {
			chair = "",
			grid = "",
			turrets = {},
			collectors = {},
			orb = "",
			cube = ""
		},

		commands = heap.new(),

		flags = {
			haveClarity = false
		},

		slivvensFound = 0
	}
end

dreadnought.empath = dreadnought.empath or {
	modules = {
		chair = "",
		grid = "",
		turrets = {},
		collectors = {},
		orb = "",
		cube = ""
	},

	commands = heap.new(),

	flags = {
		haveClarity = false
	},

	slivvensFound = 0,

	reset = function ()
		dreadnought.empath = reset()
		dreadnought.empath.reset = reset
	end
}

dreadnought.combateer = dreadnought.combateer or {
	shockwaveCalled = false,
	vortexCalled = false,
	targetAcquired = false,
	targets = {},
	flags = {
		canVortex = false
	}
}

Script.script:
function action(name, priority, command) assert(type(name) == "string") assert(type(priority) == "number") assert(type(command) == "function" or type(command) == "string")
local aethercraftCommand = function() end
if type(command) == "string" then aethercraftCommand = function() send(command) end else aethercraftCommand = function() command() end end
local action = { name = name, priority = priority, command = aethercraftCommand }
setmetatable(action, { __lt = function(a, b) return a.priority < b.priority end })
return action end

function lowerPriority(x, y) assert(type(x) == "table") assert(type(y) == "table")
assert(type(x.priority) == "number") assert(type(y.priority) == "number")
return x.priority >= y.priority end
